import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux';
import { toggleModalAction } from '../redux/actions/appActions';
import "../styles/ModalStyles.css"


interface Props {

}
interface FormDataInterface {
    "Accomodation Status": string, 
    "Rent Amount": number, 
    "Monthly Income": number, 
    "Payment Plan": string
}

const Modal = (props: Props) => {
    console.log("\n\t Modal...")
    const dispatch = useDispatch();
    const store = useSelector((store: any) => store)
    const { modalStatus } = store;
    const { showModal } = modalStatus;
    console.log("\n\t showModal: ", showModal)
    const toggleModal = () => {
        dispatch(toggleModalAction(false));
    }

    const [formData, setFormData] = useState<FormDataInterface>({
        "Accomodation Status": '', 
        "Rent Amount": 0, 
        "Monthly Income": 0, 
        "Payment Plan": ""
    })

    const accStat = ["Select accomodation status", "Self Contain", "Two Bedrooms", "Three Bedrooms"]
    const paymentPlan = ["Select payment plan", "Thirty days", "Fourty-five days", "Sixty-five days"]
    const actionBtns = ["Preview", "Cancel"];
    const fieldTypes = ['select', 'input', 'input', 'select']
    const handleInputChange = (field: string, value: string) => {
        const newData = formData;
        newData[field] = value;
    }
    return (
        <div className="modal d-flex justify-content-center align-items-center ml-5 animate__animated animate__bounce"  role="dialog" id="modal" tabIndex={-1}>
            <div className="modal-dialog" role="">
                <div className="modal-content">
                    <section className="modal-header">
                        <p className=" text-teal-500 text-2xl font-serif">Fill in the required details below</p>
                    </section>
                    <section className="modal-body">
                        {
                            Object.keys(formData).map((name, index) => (
                                <section key={name}>
                                    {
                                        fieldTypes[index] === "select" ?
                                            <article className="form-select form-group ">
                                                <label className="font-serif text-sm align-start d-flex" htmlFor="">{name}:</label>
                                                <select 
                                                    onChange={e => handleInputChange(name, e.target.value)}
                                                    className="font-serif form-control" name="" id="">
                                                    {
                                                        name.toLowerCase().includes('payment') ?
                                                        paymentPlan.map(item => (
                                                            <option  className="font-serif" key={item} value={item.toLowerCase().includes("select") ? "" : item}>{item}</option>
                                                        ))
                                                        :
                                                        accStat.map(item => (
                                                            <option className="font-serif" key={item} value={item.toLowerCase().includes("select") ? "" : item}>{item}</option>
                                                        ))
                                                    }
                                                </select>
                                            </article>
                                            :
                                            <article className="form-group">
                                                <label className="font-serif text-sm align-start d-flex" htmlFor="">{name}:</label>
                                                <input type="number" min={1} placeholder={`Enter ${name}`} className="font-serif form-control" />
                                            </article>
                                    }
                                </section>
                            ))
                        }
                    </section>
                    <section className="modal-footer">
                        {
                            actionBtns.map(btns => (
                                <button 
                                    key={btns} 
                                    data-target="#modal"
                                    data-toggle="modal"
                                    onClick={() => toggleModal()}
                                    data-dismiss={`${btns.toLowerCase() === 'cancel' ? 'modal' : undefined }`}
                                    className={`font-serif btn ${btns.toLowerCase() === 'cancel' ? 'btn-secondary' : 'btn-info'} text-white`}>
                                    {btns}
                                </button>
                            ))
                        }
                    </section>
                </div>
            </div>
        </div>
    )
}

export default Modal;
