import React from 'react'
import "../styles/ModalStyles.css"
interface Props {

}

const Modal = (props: Props) => {
  console.log("\n\t Modal...")
  const accStat = ["Select accomodation status", "Self Contain", "Two Bedrooms", "Three Bedrooms"]
  const paymentPlan = ["Select payment plan", "Thirty days", "Fourty-five days", "Sixty-five days"]
  const inputs = ["Rent Amount", "Monthly Income"];
  const actionBtns = ["Preview", "Cancel"]
    return (
        <div className="modal d-flex justify-content-center align-items-center ml-5 animate__animated animate__bounce"  role="dialog" id="modal" tabIndex={-1}>
            <div className="modal-dialog" role="">
                <div className="modal-content">
                    <section className="modal-header">
                        <p className=" text-teal-500 text-2xl font-serif">Fill in the required details below</p>
                    </section>
                    <section className="modal-body">
                        <article className="form-select form-group ">
                            <label className="font-serif text-sm align-start d-flex" htmlFor="">Accomodation Status:</label>
                            <select className="font-serif form-control" name="" id="">
                                {
                                    accStat.map(item => (
                                        <option className="font-serif" key={item} value={item.toLowerCase().includes("select") ? "" : item}>{item}</option>
                                    ))
                                }
                            </select>
                        </article>
                        {
                            inputs.map(item => (
                                <article key={item} className="form-group">
                                    <label className="font-serif text-sm align-start d-flex" htmlFor="">{item}:</label>
                                    <input type="number" min={1} placeholder={`Enter ${item}`} className="font-serif form-control" />
                                </article>
                            ))
                        }
                        <article>
                            <label className="font-serif text-sm align-start d-flex" htmlFor="">Payment Plan:</label>
                            <select className="font-serif form-control" name="" id="">
                                {
                                    paymentPlan.map(plan => (
                                        <option key={plan} value={plan.toLowerCase().includes("select") ? "" : plan}>{plan}</option>
                                    ))
                                }
                            </select>
                        </article>
                    </section>
                    <section className="modal-footer">
                        {
                            actionBtns.map(btns => (
                                <button key={btns} className={`font-serif btn ${ btn-info} text-white`}>
                                    {btns}
                                </button>
                            ))
                        }
                    </section>
                </div>
            </div>
        </div>
    )
}

export default Modal;
