import { ActionTypes, ModalActionInteface } from "../actions/actionTypes";
import { StoreInterface } from "../store";


export const modalReducer = (store: StoreInterface, action: ModalActionInteface) => {
    switch(action.type){
        case ActionTypes.SHOW_MODAL:
            store.showModal = action.payload
            return ({
                ...store
            });
        default:
            return({
                ...store
            })
    }
};

export const confirmReducer = (store: StoreInterface, action: ModalActionInteface) => {
    switch(action.type){
        case ActionTypes.SHOW_CONFIRM:
            store.showConfirm = action.payload
            return ({
                ...store
            });
        default:
            return({
                ...store
            })
    }
};


export const formDataReducer = (store: StoreInterface, action: FormData) => {
    switch(action.type){
        case ActionTypes.FORM_DATA:
            store.showConfirm = action.payload
            return ({
                ...store
            });
        default:
            return({
                ...store
            })
    }
}
