                            ))
                        }
                        <article>
                            <label className="font-serif text-sm align-start d-flex" htmlFor="">Payment Plan:</label>
                            <select className="font-serif form-control" name="" id="">
                                {
                                    paymentPlan.map(plan => (
                                        <option key={plan} value={plan.toLowerCase().includes("select") ? "" : plan}>{plan}</option>
                                    ))
                                }
                            </select>
                        </article>
                    </section>
                    <section className="modal-footer">
                        {
                            actionBtns.map(btns => (
                                <button 
                                    key={btns} 
                                    data-target="#modal"
                                    data-toggle="modal"
                                    onClick={() => toggleModal()}
                                    data-dismiss={`${btns.toLowerCase() === 'cancel' ? 'modal' : undefined }`}
                                    className={`font-serif btn ${btns.toLowerCase() === 'cancel' ? 'btn-secondary' : 'btn-info'} text-white`}>
                                    {btns}
                                </button>
                            ))
                        }
                    </section>
                </div>
            </div>
        </div>
    )
}

export default Modal;
