import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';

interface Props {

};

const RestaurantTray = (props: Props) => {
    const [restaurantState, setRestaurantState] = useState({
        inventoryCategory: [] as any,
        categoryVarieties: [] as any,
        varietyMenus: [] as any,
        tableOrder : [] as any,
        restaurantCategoryNames: [] as any,
        categoryVarietiesUnderChosenCategory: [] as any,
        varietyMenuUnderChosenCategoryVariety: [] as any,
        loadingPrompt: "Restaurant is setting things up. Please wait",
        restaurantStateInputs: {}
    });

    const handleRestaurantInputChange = (field: string, value: string) => {
        (restaurantState.restaurantStateInputs as any)[field] = value;
        setRestaurantState({
            ...restaurantState
        })
    }
    const dispatch = useDispatch();
    const store = useSelector((store: any) => store);
    const { confirmModal, modulesData } = store
    const buttonActions = ["Place Order", "Cancel Order"];

    const removeItemFromTray = (itemsIndex: number) => {
        console.log("\n\t itemIndexToremove: ", itemsIndex)
        console.log("\n\t restaurantState.tableOrder: ", restaurantState.tableOrder);
        const desiredOrder = [] as any;
        (restaurantState.tableOrder as string[]).filter((item, index) => {
            if(index !== itemsIndex){
                desiredOrder.push(item)
            }
        });
        console.log("\n\t desiredOrder: ", desiredOrder);
        let total = 0;
        desiredOrder.map((item: any) => {
            total += item.cost
        });
        setRestaurantState({
            ...restaurantState,
            tableOrder: desiredOrder,
            restaurantStateInputs: {
                ...restaurantState.restaurantStateInputs,
                total
            }
        })
    }
    const setTableItems = () => {
        const varietyMenu = (document as any).querySelector('.variety-menu').value;
        const quantity = (restaurantState.restaurantStateInputs as any)['quantity']
        let cost = 0;
        restaurantState.varietyMenus.find((item: any) => {
            if(item.varietyMenu === varietyMenu) {
                return cost = item.cost;
            }
        })
        console.log("\n\t setTableItems-varietyMenu: ", varietyMenu);
        const chosenItem = {
            item: varietyMenu,
            quantity: quantity ? quantity : 1,
            cost
        };
        let total = 0;
        restaurantState.tableOrder.map((item: any) => {
            total += item.cost
        });
        console.log("\n\t Total-1: ", total)
        // compute the cost of the last added item
        const lastItem = restaurantState.varietyMenus.find((item: any) => item.varietyMenu === varietyMenu)
        total += Number(lastItem.cost);
        console.log("\n\t Total: ", total)
        varietyMenu &&
        setRestaurantState({
            ...restaurantState,
            tableOrder : [
                ...restaurantState.tableOrder ,
                chosenItem
            ],
            restaurantStateInputs: {
                ...restaurantState.restaurantStateInputs,
                total
            }
        })
        console.log("\n\t varietyMenuUnderChosenCategoryVariety: ", restaurantState.varietyMenuUnderChosenCategoryVariety[0])
    };

    const setCategoryVariety = (categoryName: string) => {
        const selectedCategory = restaurantState.inventoryCategory.find((cat: any) => cat.categoryName === categoryName)
        const requiredVarieties = restaurantState.categoryVarieties.filter((catVariety: any) => String(catVariety.inventoryCategoryDetails) === String(selectedCategory._id))
        const varietiesUnderChosenCategory = requiredVarieties.map((cat: any) => cat.categoryVarietyName);
        console.log("\n\t restaurantData-varietiesUnderChosenCategory: ", varietiesUnderChosenCategory);
        setRestaurantState({
            ...restaurantState,
            categoryVarietiesUnderChosenCategory: [
                ...varietiesUnderChosenCategory
            ]
        })
    };
    const setVarietyMenuOptions = (categoryVariety: string) => {
        console.log("\n\t restaurantData-categoryVariety: ", categoryVariety);
        console.log("\n\t restaurantState.categoryVarieties: ", restaurantState.categoryVarieties);
        const selectedCategoryVariety = restaurantState.categoryVarieties.find((cat: any) => cat.categoryVarietyName === categoryVariety)
        console.log("\n\t selectedCategoryVariety: ", selectedCategoryVariety);
        const requiredCategoryVariety = restaurantState.varietyMenus.filter((varietyMenu: any) => String(varietyMenu.inventoryVarietyDetails) === String(selectedCategoryVariety._id))
        const varietyMenuUnderChosenCategoryVariety = requiredCategoryVariety.map((cat: any) => cat.varietyMenu);
        console.log("\n\t varietyMenuUnderChosenCategoryVariety: ", varietyMenuUnderChosenCategoryVariety);
        setRestaurantState({
            ...restaurantState,
            varietyMenuUnderChosenCategoryVariety: [
                ...varietyMenuUnderChosenCategoryVariety,
            ]
        })
    };
    // console.log("\n\t varietyMenu: ", restaurantState.varietyMenuUnderChosenCategoryVariety);

    // console.log("\n\t modules: ", modules && modules);
    const fieldNames = ['Category', 'Variety', 'Variety Menu', 'Quantity'];
    const fieldTypes = ['select', 'select', 'select', 'input'];
    return (
        <div className="modal fade d-flex justify-content-center align-items-center ml-5 tray-container animate__animated animate__bounce"  aria-labelledby="exampleModalLabel" role="dialog" id="restaurantTray" tabIndex={-1}>
            <div className="container">
                <p className="text-white">{restaurantState.loadingPrompt}</p>
                <section style={{justifyContent: "space-around", width: '100%'}} className="row d-flex mt-3 align-items-center">
                    {
                        fieldNames.map((fieldName, index) => (
                            <article key={fieldName} className="form-group col-md-3 category">
                                {
                                    fieldTypes[index] === 'select' ?
                                        <article>
                                            <label>{fieldName}:</label>
                                            <select onChange={
                                                fieldName.toLowerCase() === 'category' ?
                                                e => setCategoryVariety(e.target.value) : 
                                                fieldName.toLowerCase() === 'variety' ?
                                                e => setVarietyMenuOptions(e.target.value) :
                                                undefined 
                                                } className={`form-select form-control ${fieldName.toLowerCase().replaceAll(" ", "-")}`} name="" id="">
                                                <option value="">Select {fieldName}</option>
                                                {
                                                    fieldName.toLowerCase() === 'category' && restaurantState.restaurantCategoryNames ?
                                                    restaurantState.restaurantCategoryNames.map((cat: any) => (
                                                        <option key={cat} value={cat}>{cat}</option>
                                                    ))
                                                    :
                                                    fieldName.toLowerCase() === 'variety' && restaurantState.categoryVarietiesUnderChosenCategory ?
                                                    restaurantState.categoryVarietiesUnderChosenCategory.map((variety: any) => (
                                                        <option key={variety} value={variety}>{variety}</option>
                                                    ))
                                                    :
                                                    fieldName.toLowerCase().includes('menu') && restaurantState.varietyMenuUnderChosenCategoryVariety &&
                                                    restaurantState.varietyMenuUnderChosenCategoryVariety.map((menu: any) => (
                                                        <option key={menu} value={menu}>{menu}</option>
                                                    ))
                                                }
                                            </select>
                                        </article>
                                        :
                                        <article className={`${fieldName.toLowerCase().replaceAll(" ", "-")}`}>
                                            <label>{fieldName}:</label>
                                            <input onChange={e => handleRestaurantInputChange(`${fieldName.toLowerCase().replaceAll(" ", "-")}`, e.target.value)} min={1} className="form-control" type="number" />
                                        </article>
                                }

                            </article>
                        ))
                    }
                    <article className="btn-container">
                        <button 
                            onClick={e => setTableItems()}
                            className="btn btn-info mt-1 mb-3">
                            Add to order
                        </button>
                    </article>
                </section>
                <section className="row d-flex justify-content-center">
                <div style={{width: "40vw"}} className="table-responsive row d-flex align-self-center">
                    <table className="table table-hover table-striped table-bordered ">
                        <thead className="text-center">
                            <tr><th colSpan={13}>Order For {}</th></tr>
                            <tr className="text-center col-md-12">
                                <th className="" colSpan={1}>S/N</th>
                                <th className="" colSpan={4}>Item</th>
                                <th className="" colSpan={3}>Quantity</th>
                                <th className="" colSpan={3}>Cost</th>
                                <th className="" colSpan={1}></th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                restaurantState.tableOrder && restaurantState.tableOrder.map((order: any, index: number) => (
                                    <tr key={index} className="text-center">
                                        <td colSpan={1}>{index+1}</td>
                                        <td colSpan={4}>{order.item}</td>
                                        <td colSpan={3}>{order.quantity}</td>
                                        <td colSpan={3}>{order.cost.toLocaleString()}</td>

                                    </tr>
                                ))
                            }
                        </tbody>
                        {
                            (restaurantState.restaurantStateInputs as any)['total'] &&
                            <tfoot className="">
                                <tr className="text-center">
                                    <td colSpan={1} className="text-center font-weight-bold">Total</td>
                                    <td colSpan={4}></td>
                                    <td colSpan={3}></td>

                                    <td colSpan={3}>{(restaurantState.restaurantStateInputs as any)['total'] && (restaurantState.restaurantStateInputs as any)['total'].toLocaleString()}</td>
                                    <td colSpan={1}></td>

                                </tr>
                            </tfoot>
                        }
                    </table>
                </div>
                </section>
                <section className="btn-container float-right">
                    {
                        buttonActions.map(action => (
                            <button
                                // onClick={e => handleTrayBtnClick(action)} 
                                key={action}
                                data-toggle="modal"
                                data-target="#restaurantTray"
                                data-dismiss={action.toLowerCase().includes('cancel') ? "modal" : undefined}
                                className={`btn btn-${action.toLowerCase().includes('cancel') ? 'secondary' : 'info'} mx-2`}>
                                {action}
                            </button>
                        ))
                    }
                </section>
            </div>
        </div>

    )
};

export default RestaurantTray;
