import { ActionTypes, ModalActionInteface } from "./actionTypes"


export const toggleModalAction = (modalStatus: boolean) => {
    return<ModalActionInteface>({
        type: ActionTypes.SHOW_MODAL,
        payload: modalStatus
    });
};
